parameters.jsCode:
const leadData = $input.first().json.body;
const {
  userId,
  eventType,
  budgetRange,
  eventDate,
  attendees,
  company,
  clientEmail,
  clientName,
  location,
  specialRequests,
  celebrityId,
  userHistory = {}
} = leadData;

// Initialize scoring
let score = 0;
let qualificationReasons = [];
let disqualificationReasons = [];

// Budget scoring (40% of total score)
const budgetScores = {
  'under-25k': 10,
  '25k-50k': 20,
  '50k-100k': 30,
  '100k-250k': 35,
  '250k-500k': 40,
  '500k+': 40
};
score += budgetScores[budgetRange] || 0;
if (budgetScores[budgetRange] >= 30) {
  qualificationReasons.push(`High budget range: ${budgetRange}`);
} else if (budgetScores[budgetRange] <= 20) {
  disqualificationReasons.push(`Low budget range: ${budgetRange}`);
}

// Event type scoring (15% of total score)
const eventTypeScores = {
  'Corporate Event': 15,
  'Product Launch': 15,
  'Award Ceremony': 12,
  'Conference': 12,
  'Charity Event': 10,
  'Wedding': 8,
  'Private Party': 8,
  'Birthday Party': 5,
  'Virtual Event': 10,
  'Other': 3
};
score += eventTypeScores[eventType] || 0;
if (eventTypeScores[eventType] >= 12) {
  qualificationReasons.push(`Professional event type: ${eventType}`);
}

// Company presence (10% of total score)
if (company && company.trim().length > 0) {
  score += 10;
  qualificationReasons.push('Company/organization provided');
}

// Attendee count scoring (10% of total score)
const attendeeScores = {
  '1-50': 3,
  '51-100': 5,
  '101-500': 8,
  '501-1000': 10,
  '1000+': 10
};
score += attendeeScores[attendees] || 0;
if (attendeeScores[attendees] >= 8) {
  qualificationReasons.push(`Large event size: ${attendees} attendees`);
}

// Timing scoring (10% of total score)
if (eventDate) {
  const eventDateObj = new Date(eventDate);
  const now = new Date();
  const daysAhead = Math.ceil((eventDateObj - now) / (1000 * 60 * 60 * 24));

  if (daysAhead >= 30 && daysAhead <= 180) {
    score += 10; // Ideal planning window
    qualificationReasons.push('Optimal booking timeline (30-180 days ahead)');
  } else if (daysAhead >= 14 && daysAhead < 30) {
    score += 7; // Good planning
    qualificationReasons.push('Good booking timeline (2-4 weeks ahead)');
  } else if (daysAhead > 180) {
    score += 5; // Too far ahead
  } else if (daysAhead < 14) {
    score += 2; // Rush booking
    disqualificationReasons.push('Very short notice booking (less than 2 weeks)');
  }
}

// Location scoring (5% of total score)
const majorCities = ['Los Angeles', 'New York', 'Chicago', 'Miami', 'Las Vegas', 'Atlanta', 'Nashville', 'London', 'Toronto'];
if (location && majorCities.some(city => location.toLowerCase().includes(city.toLowerCase()))) {
  score += 5;
  qualificationReasons.push('Event in major market');
}

// User history scoring (10% of total score)
if (userHistory.previousBookings > 0) {
  score += 5;
  qualificationReasons.push('Returning customer');

  if (userHistory.totalSpent > 50000) {
    score += 5;
    qualificationReasons.push('High-value customer history');
  }

  if (userHistory.averageRating >= 4.5) {
    qualificationReasons.push('Excellent customer rating history');
  } else if (userHistory.averageRating < 3.0) {
    disqualificationReasons.push('Poor customer rating history');
  }
}

// Email domain scoring (bonus points)
if (clientEmail) {
  const domain = clientEmail.split('@')[1];
  const corporateDomains = ['gmail.com', 'yahoo.com', 'hotmail.com', 'outlook.com'];

  if (!corporateDomains.includes(domain.toLowerCase())) {
    score += 3; // Corporate or custom domain
    qualificationReasons.push('Professional email domain');
  }
}

// Special requests analysis (bonus/penalty)
if (specialRequests && specialRequests.length > 100) {
  score += 2; // Detailed requirements show serious intent
  qualificationReasons.push('Detailed event requirements provided');
}

// Determine qualification level
let qualificationLevel;
let priority;
let autoActions = [];

if (score >= 70) {
  qualificationLevel = 'Hot Lead';
  priority = 'high';
  autoActions.push('immediate_followup', 'assign_premium_agent', 'expedite_quote');
} else if (score >= 50) {
  qualificationLevel = 'Warm Lead';
  priority = 'medium';
  autoActions.push('followup_within_2hours', 'assign_experienced_agent');
} else if (score >= 30) {
  qualificationLevel = 'Cold Lead';
  priority = 'low';
  autoActions.push('followup_within_24hours', 'send_info_packet');
} else {
  qualificationLevel = 'Poor Lead';
  priority = 'very_low';
  autoActions.push('automated_response_only', 'nurture_campaign');
  disqualificationReasons.push('Overall low qualification score');
}

// Risk assessment
let riskFactors = [];
if (disqualificationReasons.length > 2) {
  riskFactors.push('Multiple disqualification factors');
}
if (score < 20) {
  riskFactors.push('Very low lead score');
}
if (eventDate) {
  const daysAhead = Math.ceil((new Date(eventDate) - new Date()) / (1000 * 60 * 60 * 24));
  if (daysAhead < 7) {
    riskFactors.push('Extremely short notice');
  }
}

return {
  leadId: userId + '-' + Date.now(),
  userId,
  score,
  qualificationLevel,
  priority,
  qualificationReasons,
  disqualificationReasons,
  riskFactors,
  autoActions,
  analysis: {
    budgetScore: budgetScores[budgetRange] || 0,
    eventTypeScore: eventTypeScores[eventType] || 0,
    totalPossibleScore: 100
  },
  recommendedActions: {
    immediate: autoActions.filter(action => action.includes('immediate')),
    followup: autoActions.filter(action => action.includes('followup')),
    automation: autoActions.filter(action => !action.includes('immediate') && !action.includes('followup'))
  },
  originalData: leadData,
  scoredAt: new Date().toISOString()
};

